mapper.select:
select count(*)
from dy_movies.vod_detail
where flag = #{flag}
  and tid = #{tid}

select count(*)
from dy_movies.vod
where flag = #{flag}
  and vid = #{vid}



    and v.tid = #{tid}


    and v.tid in (select id from dy_movies.vod_type where flag = #{flag} and pid = #{tid})

order by v.update_time desc
limit #{pageOffset}, #{pageSize}
