mapper.select:
select v.id, v.vid, v.name, v.tid, v.flag, v.note, v.update_time, vd.pic, vd.year, vd.director, vd.lang, vd.area, vd.actor from dy_movies.vod v left join dy_movies.vod_detail vd on v.vid = vd.vid and v.flag = vd.flag where v.flag = #{flag} and name like concat('%', #{keyword}, '%')
and v.tid in (select id from dy_movies.vod_type where flag = #{flag} and pid = #{tid})
order by vd.year, v.update_time desc limit #{pageOffset}, #{pageSize}

select count(*) from dy_movies.vod v where v.flag = #{flag}
and v.tid in (select id from dy_movies.vod_type where flag = #{flag} and pid = #{tid})
and name like concat('%', #{keyword}, '%')
